import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  ngOnInit(): void {
    let refreshDone: any = localStorage.getItem('refreshDone');
    if (refreshDone === 'true') { // Perbandingan harus menggunakan ===, bukan =
      // Lakukan refresh halaman jika belum dilakukan sebelumnya
      location.reload();
      localStorage.setItem('refreshDone', 'false');
    } else {
      // Jika tidak ada atau sudah 'false', atur ke 'true' untuk refresh berikutnya
      localStorage.setItem('refreshDone', 'true');
    }

  }

}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Responsive Sidebar</title>
  <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.5.2/css/bootstrap.min.css">
  <style>
    /* Sidebar */
    .sidebar {
      position: fixed;
      top: 0;
      left: 0;
      height: 100%;
      width: 250px;
      background-color: #343a40;
      padding-top: 50px;
      transition: all 0.3s;
    }

    .sidebar ul.navbar-nav li.nav-item {
      width: 100%;
    }

    .sidebar a.nav-link {
      color: #fff;
      padding: 10px 15px;
      text-align: center;
      display: block;
    }

    .sidebar a.nav-link:hover {
      background-color: #495057;
    }

    /* Content */
    .content {
      margin-left: 250px;
      transition: all 0.3s;
    }

    /* Responsive */
    @media (max-width: 768px) {
      .sidebar {
        width: 0;
        overflow: hidden;
      }

      .content {
        margin-left: 0;
      }
    }
  </style>
</head>
<body>

<div class="sidebar">
  <ul class="navbar-nav">
    <li class="nav-item"><a href="#" class="nav-link">Home</a></li>
    <li class="nav-item"><a href="#" class="nav-link">About</a></li>
    <li class="nav-item"><a href="#" class="nav-link">Services</a></li>
    <li class="nav-item"><a href="#" class="nav-link">Contact</a></li>
  </ul>
</div>

<div class="content">
  <nav class="navbar navbar-expand-lg navbar-dark bg-dark">
    <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>

    <div class="collapse navbar-collapse" id="navbarSupportedContent">
      <ul class="navbar-nav mr-auto">
        <li class="nav-item"><a href="#" class="nav-link">Home</a></li>
        <li class="nav-item"><a href="#" class="nav-link">About</a></li>
        <li class="nav-item"><a href="#" class="nav-link">Services</a></li>
        <li class="nav-item"><a href="#" class="nav-link">Contact</a></li>
      </ul>
    </div>
  </nav>
  <div class="container mt-4">
    <h2>Main Content</h2>
    <p>This is the main content area. It will adjust when the sidebar is toggled.</p>
  </div>
</div>

<script src="https://code.jquery.com/jquery-3.5.1.slim.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/@popperjs/core@2.5.4/dist/umd/popper.min.js"></script>
<script src="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/js/bootstrap.min.js"></script>
</body>
</html>
