import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  ngOnInit(): void {
    let refreshDone: any = localStorage.getItem('refreshDone');
    if (refreshDone === 'true') { // Perbandingan harus menggunakan ===, bukan =
      // Lakukan refresh halaman jika belum dilakukan sebelumnya
      location.reload();
      localStorage.setItem('refreshDone', 'false');
    } else {
      // Jika tidak ada atau sudah 'false', atur ke 'true' untuk refresh berikutnya
      localStorage.setItem('refreshDone', 'true');
    }

  }

}
