import { Component } from '@angular/core';

@Component({
  selector: 'app-hero',
  templateUrl: './hero.component.html',
  styleUrls: ['./hero.component.css']
})
export class HeroComponent {
  typedText: any = '';
  nama: string = '';
  email: string = '';
  password: string = '';
  message: string = '';

  constructor() {
    // Set your desired text
    const textToType: string = "apa ada hal yang bisa saya bantu tuan ? ";
    this.typeText(textToType, 0);
  }

  typeText(text: string, index: number) {
    this.typedText = text.substring(0, index);
    if (index < text.length) {
      setTimeout(() => {
        this.typeText(text, index + 1);
      }, 100); 
    }
  }

  handleClick(){
    if (!this.nama || !this.email || !this.password) {
      this.message = 'Semua field harus diisi!';
    } else {
      this.message = 'Form berhasil dikirim!';
      // Lakukan tindakan lanjutan seperti mengirim data ke backend, dsb.
    }
  }
}
