@Component({
  selector: 'app-prompt',
  templateUrl: './prompt.component.html',
  styleUrls: ['./prompt.component.css']
})
export class PromptComponent implements OnInit {
  prompt: string = '';
  message: string ='';
  ngOnInit(): void {

  }
  sent(){
    if(!this.prompt){
      this.message = "field tidak boleh kosong !"
    }
  }

}
